mapper.update:
   		update tbl_comnotice set ctitle = #{ctitle}
   						   	   , ccontent = #{ccontent}
		where cnotice_seq = #{cnotice_seq}

   		update tbl_deptnotice set dtitle = #{dtitle}
   						   	   , dcontent = #{dcontent}
		where dnotice_seq = #{dnotice_seq}

   		update tbl_formboard set ftitle = #{ftitle}
   						   	   , fcontent = #{fcontent}
		where form_seq = #{form_seq}
